-- ----------------------------
-- Table structure for banners
-- ----------------------------
DROP TABLE IF EXISTS `banners`;
CREATE TABLE `banners`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `title` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci NOT NULL,
  `slug` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `url` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `target` int(10) UNSIGNED NULL DEFAULT NULL,
  `description` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `type` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `position` int(10) UNSIGNED NOT NULL DEFAULT 0,
  `is_active` int(10) UNSIGNED NOT NULL DEFAULT 1,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `banners_slug_unique`(`slug`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 16 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of banners
-- ----------------------------
INSERT INTO `banners` VALUES (12, 'Ecogreen', 'ecogreen', 'uploads/banner/1589376661_pexels-photo-461428.jpeg', '/', 1, '<p>C&ugrave;ng ch&uacute;ng t&ocirc;i tạo n&ecirc;n một Tr&aacute;i đất ng&agrave;y c&agrave;ng xanh nhiều v&agrave; hơn thế nữa.</p>', 1, 1, 1, '2020-05-13 13:31:01', '2020-05-14 14:44:23');
INSERT INTO `banners` VALUES (13, 'Our action', 'our-action', 'uploads/banner/1589377191_our-action.jpg', NULL, 1, '<p>H&agrave;nh động c&ugrave;ng ch&uacute;ng m&igrave;nh. Mỗi c&acirc;y xanh được trồng xuống l&agrave; 1&nbsp;chấm xanh được t&ocirc; điểm tr&ecirc;n h&igrave;nh cầu Tr&aacute;i đất.</p>', 1, 2, 1, '2020-05-13 13:39:51', '2020-05-13 13:39:51');
INSERT INTO `banners` VALUES (14, 'Người Việt bắt đầu sống tối giản từ đâu ?', 'nguoi-viet-bat-dau-song-toi-gian-tu-dau', 'uploads/banner/1589467680_Minimalism_Feature_2.jpg', 'http://ecogreen.co/blog/5', 1, '<p>Những bước tối giản h&oacute;a cuộc sống đầu ti&ecirc;n để người Việt hạnh ph&uacute;c, khỏe mạnh hơn.</p>', 1, 3, 1, '2020-05-14 14:48:00', '2020-05-14 14:48:00');
INSERT INTO `banners` VALUES (15, 'Ngoài giảm nhựa, chúng ta còn cách nào để sống xanh?', 'ngoai-giam-nhua-chung-ta-con-cach-nao-de-song-xanh', 'uploads/banner/1589685141_song-xanh-featured.jpg', 'http://ecogreen.co/blog/7', 1, '<p>Sống xanh vốn kh&ocirc;ng phải điều g&igrave; qu&aacute; phức tạp, nhưng cũng kh&ocirc;ng chỉ g&oacute;i gọn trong việc giảm nhựa như suy nghĩ của nhiều người.</p>', 1, 4, 1, '2020-05-17 03:12:21', '2020-05-17 03:12:21');

