INSERT INTO `products` VALUES (77, 'Tinh dầu thơm Bạc hà thiên nhiên 10/20/50ml', 'tinh-dau-thom-bac-ha-thien-nhien-102050ml', 'uploads/product/1587551673_tinh dầu bạc hà.jpg', 10, 38000, 35500, 10, 1, 1, 0, 51, NULL, NULL, 24, 12, NULL, '<p>Tinh dầu Hương Nguy&ecirc;n được chiết xuất 100% nguy&ecirc;n chất bằng phương ph&aacute;p chưng cất hơi nước, kh&ocirc;ng chứa chất bảo quản, đảm bảo chất lượng v&agrave; an to&agrave;n cho sức khỏe người sử dụng. - Thư gi&atilde;n tinh thần, lọc kh&ocirc;ng kh&iacute;: nhỏ 1 - 3 giọt tinh dầu v&agrave;o đ&egrave;n x&ocirc;ng tinh dầu để x&ocirc;ng hương.<br />\r\n- Giảm đau nhức xương khớp, bong g&acirc;n v&agrave; hỗ trợ ti&ecirc;u h&oacute;a: Nhỏ 10 - 15 giọt tinh dầu với 30ml dầu nền, massage nhẹ nh&agrave;ng c&aacute;c v&ugrave;ng bong g&acirc;n, đau nhức cơ khớp để xoa dịu giảm đau hoặc massage v&ugrave;ng bụng theo chiều kim đồng hồ để hỗ trợ tốt hệ ti&ecirc;u h&oacute;a,- X&ocirc;ng hơi da mặt: Nhỏ 1 - 3 giọt tinh dầu v&agrave;o chậu nước n&oacute;ng, d&ugrave;ng khăn tr&ugrave;m k&iacute;n mặt v&agrave; chậu nước, x&ocirc;ng hơi trong 15p.<br />\r\n- Hỗ trợ hoạt động tự nhiện của c&aacute;c khớp, giải tỏa căng thẳng mệt mỏi: Nhỏ 5 giọt v&agrave;o bồn tắm v&agrave; ng&acirc;m trong 15 - 30p.</p>', NULL, NULL, 0, '2020-04-22 10:33:13', '2020-04-23 01:59:55');
INSERT INTO `products` VALUES (78, 'Lược tre', 'luoc-tre', 'uploads/product/1587654335_lược tre.jpg', 10, 39000, 35000, 11, 1, 1, 0, 52, 'https://www.laidayrefill.com/product-page/lược-tre-bamboo-comb', '1', 25, 12, NULL, '<p>Xuất xứ: Việt Nam.</p>\r\n\r\n<p>Gi&aacute;: 39,000 Đ.</p>\r\n\r\n<p>K&iacute;ch thước: 13,5 x 3 cm.</p>\r\n\r\n<p>Trọng lượng: 9 gram.</p>\r\n\r\n<p>Chất liệu: 100% tre tự nhi&ecirc;n từ v&ugrave;ng trồng tre tại Thanh Ho&aacute;, miền Bắc Việt Nam</p>', NULL, NULL, 0, '2020-04-23 15:04:23', '2020-04-23 15:05:35');
INSERT INTO `products` VALUES (79, 'Bàn chải tre', 'ban-chai-tre', 'uploads/product/1587654398_bàn chải tre.jpg', 10, 25000, 22000, 12, 1, 1, 0, 52, 'https://shopee.vn/Bàn-chải-tre-thân-thiện-môi-trường-i.136064100.2053411320', '1', 21, 13, NULL, '<p>B&agrave;n chải tre được l&agrave;m từ nguy&ecirc;n liệu th&acirc;n thiện với m&ocirc;i trường ,c&oacute; khả năng kh&aacute;ng khuẩn tự nhi&ecirc;n v&agrave; tự ph&acirc;n hủy . L&ocirc;ng b&agrave;n chải rất mềm,an to&agrave;n khi sử dụng . Sau khi đ&aacute;nh răng xong nhớ để nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t để tr&aacute;nh ẩm mốc c&aacute;n b&agrave;n chải tre bạn nh&eacute;. B&agrave;n chải đ&aacute;nh răng bằng tre l&agrave; một c&aacute;ch tuyệt vời để gi&uacute;p cho tr&aacute;i đất xanh.</p>', NULL, NULL, 0, '2020-04-23 15:05:41', '2020-04-23 15:06:38');
INSERT INTO `products` VALUES (80, 'Muôi xới cơm bằng gỗ', 'muoi-xoi-com-bang-go', 'uploads/product/1587654725_muôi xới cơm.jpg', 10, 43000, 39000, 15, 1, 1, 0, 51, 'https://shopee.vn/Muôi-xới-cơm-bằng-gỗ-tự-nhiên-Chịu-nhiệt-cao-HAHANCO-CTH721-i.74792135.4607713089', '1', 21, 13, NULL, '<p>sản phẩm được sản xuất thủ c&ocirc;ng, họa tiết v&agrave; k&iacute;ch thước c&oacute; thể được thay đổi bởi nh&agrave; sản xuất cho hợp xu hướng.</p>\r\n\r\n<p>👉 Gỗ tự nhi&ecirc;n tr&ecirc;n th&acirc;n mu&ocirc;i m&agrave;u sắc kh&ocirc;ng giống nhau, kh&ocirc;ng ảnh hưởng g&igrave; đến chất lượng (m&agrave;u ngẫu nhi&ecirc;n).</p>\r\n\r\n<p>👉 Thiết kế trang nh&atilde; v&agrave; tinh tế.</p>\r\n\r\n<p>👉 Sản phẩm nhẹ, đẹp, bền, chống d&iacute;nh, chống nấm mốc, kh&ocirc;ng ho&aacute; chất.</p>\r\n\r\n<p>👉 Chất gỗ cứng bền c&oacute; thể tồn tại với thời gian, v&acirc;n gỗ đẹp, c&oacute; khả năng chống lại mối mọt cao v&agrave; chịu được nhiệt độ cao.</p>\r\n\r\n<p>👉Trải qua qu&aacute; tr&igrave;nh đ&aacute;nh b&oacute;ng thủ c&ocirc;ng tỉ mỉ, kh&ocirc;ng chứa ho&aacute; chất, kh&ocirc;ng g&acirc;y t&aacute;c hại đến thức ăn, đảm bảo an to&agrave;n thực phẩm tốt cho sức khoẻ người d&ugrave;ng. 👉 Sản phẩm c&oacute; độ trơn nhẵn, chắc chắn cao, kh&ocirc;ng thấm nước, kh&ocirc;ng b&aacute;m m&ugrave;i thức ăn, g&oacute;p phần hạn chế tối đa vi khuẩn v&agrave; nấm mốc g&acirc;y hại.</p>\r\n\r\n<p>👉 Thiết kế c&aacute;n d&agrave;i để cầm chắc tay, c&aacute;ch nhiệt tốt, bảo vệ cho đ&ocirc;i tay kh&ocirc;ng bị bỏng, rất tiện lợi v&agrave; dễ sử dụng.</p>\r\n\r\n<p>👉 Độ cứng v&agrave; chịu lực cao.</p>\r\n\r\n<p>👉 D&ugrave;ng được cho m&aacute;y rửa ch&eacute;n.</p>\r\n\r\n<p>👉 An to&agrave;n cho sức khoẻ, th&acirc;n thiện với m&ocirc;i trường.</p>\r\n\r\n<p>👉 Thiết kế nhỏ gọn với tay cầm vừa vặn v&agrave; c&oacute; khả năng chống trơn hiệu quả. Phần l&ograve;ng mu&ocirc;i c&oacute; độ khum vừa phải gi&uacute;p việc lấy cơm dễ d&agrave;ng.</p>', NULL, NULL, 0, '2020-04-23 15:10:35', '2020-04-23 15:12:05');
INSERT INTO `products` VALUES (81, 'Làn tre', 'lan-tre', 'uploads/product/1587806200_làn tre.jpg', 10, 250000, 220000, 16, 1, 1, 0, 54, 'https://shopee.vn/Làn-tre-quai-mây-i.45788492.935494476', '1', 21, 13, NULL, '<p>Sản phẩm thủ c&ocirc;ng đẹp v&agrave; bền th&igrave; kh&acirc;u nguy&ecirc;n liệu v&agrave; kh&eacute;o tay l&agrave; điều chắc chắn.</p>\r\n\r\n<p>L&agrave;n tre quai m&acirc;y hội tụ đủ những yếu tố đ&oacute;.</p>\r\n\r\n<p>Những c&acirc;y giang (họ tre) được chọn lựa đ&uacute;ng độ, chẻ tay đều chằn chặn.</p>\r\n\r\n<p>Những sợi m&acirc;y được uốn lại &ocirc;m lấy chiếc giỏ tre rồi tạo h&igrave;nh th&agrave;nh quai.</p>\r\n\r\n<p>K&iacute;ch cỡ (+/-3cm):</p>\r\n\r\n<ul>\r\n	<li>Đ&aacute;y 18x20cm.</li>\r\n	<li>Miệng 23 x 27cm.</li>\r\n	<li>Cao 26cm.</li>\r\n	<li>quai 15cm.</li>\r\n</ul>', NULL, NULL, 0, '2020-04-25 09:12:09', '2020-04-25 09:16:40');
INSERT INTO `products` VALUES (82, 'Chiếu Tre', 'chieu-tre', 'uploads/product/1587826694_chiếu tre.jpg', 10, 262000, 238000, 17, 1, 1, 0, 51, 'https://shopee.vn/Chiếu-Tre-Giường-Tầng-Chiếu-Trúc-Tăm-Chống-Mốc-i.158747598.4404029070', '1', 21, 13, NULL, '<p>🍀 Chiếu Tre Giường Tầng, Chiếu Tr&uacute;c Tăm Chống Mốc</p>\r\n\r\n<p>🍀Một sản phẩm chống lại cả một m&ugrave;a h&egrave; oi bức...Chiếu đầu m&ugrave;a n&ecirc;n si&ecirc;u đẹp.</p>\r\n\r\n<p>🍀Chiếu cực đẹp, c&aacute;c sợi tre đ&atilde; được m&agrave;i nhẵn một c&aacute;ch rất c&ocirc;ng phu kh&ocirc;ng c&oacute; c&aacute;c sợi dằm nhỏ đ&acirc;m v&agrave;o người đ&acirc;u ạ. C&aacute;c thanh tre v&agrave;ng &oacute;ng được gắn kết với nhau rất chắc chắn kh&ocirc;ng c&oacute; khe hở n&ecirc;n kh&ocirc;ng sợ bị kẹp t&oacute;c, chiếu nằm si&ecirc;u m&aacute;t với độ bền cực cao.</p>\r\n\r\n<p>🍀Chiếu tr&uacute;c tăm v&acirc;n gỗ 👉K&iacute;ch Thước: 80x190cm. Chi&ecirc;́u được đan ghép bằng những sợi d&acirc;y dù chắc chắn, viền chi&ecirc;́u đẹp, có đ&ocirc;̣ b&ecirc;̀n cao. C&aacute;c thanh Tre được tuốt rất nhẵn, mịn, đều nhau, v&agrave; được xếp kh&iacute;t, đẹp, nhẵn tạo cảm gi&aacute;c &ecirc;m &aacute;i v&agrave; thoải m&aacute;i khi nằm. Đồng thời gi&uacute;p bạn dễ vệ sinh khi bị bẩn, chỉ cần lau l&agrave; sạch. C&aacute;c thanh tre sau khi được sấy kh&ocirc;, đ&aacute;nh b&oacute;ng, ch&agrave; mịn được đan kh&iacute;t với nhau bởi d&acirc;y d&ugrave; chắc bền.</p>\r\n\r\n<p>🍀Viền ngo&agrave;i chiếu được may đều đặn, đẹp v&agrave; rất chắc chắn. Chiếu tre gỗ rất dễ d&agrave;ng cuộn tr&ograve;n để bảo quản sau khi d&ugrave;ng.</p>', NULL, NULL, 0, '2020-04-25 09:16:46', '2020-04-25 14:58:14');
INSERT INTO `products` VALUES (83, 'Bóng Đèn Led Tiết Kiệm Điện', 'bong-den-led-tiet-kiem-dien', 'uploads/product/1587826908_đèn led.jpg', 10, 53000, 46000, 18, 1, 1, 0, 51, 'https://shopee.vn/Bóng-đèn-LED-Tiết-Kiệm-Năng-Lượng-Siêu-Sáng-Công-Suất-Cao-Trụ-Trắng-5W-10W-15W-20W-30W-40W-50W-SZ9-i.126592641.5007941597', '1', 26, 13, NULL, '<p>Thiết kế nhỏ gọn, dễ lắp đặt.</p>\r\n\r\n<p>C&ocirc;ng nghệ chiếu s&aacute;ng ti&ecirc;u chuẩn: ti&ecirc;u thụ điện thấp, &iacute;t tỏa nhiệt v&agrave; c&oacute; tuổi thọ cao.</p>\r\n\r\n<p>Ph&ugrave; hợp nhiều kh&ocirc;ng gian, th&acirc;n thiện với m&ocirc;i trường.</p>\r\n\r\n<ul>\r\n	<li>&Aacute;nh s&aacute;ng trắng (3000-3200K).</li>\r\n	<li>C&ocirc;ng suất: 5W, 10W, 15W, 20W, 30W, 40W, 50W&nbsp;(t&ugrave;y chọn).</li>\r\n	<li>Điện &aacute;p: 220V/ 50HZ.</li>\r\n	<li>Quang th&ocirc;ng: 4000LM.</li>\r\n	<li>Nhiệt độ m&agrave;u: 6500K.</li>\r\n	<li>Đu&ocirc;i đ&egrave;n: E27 Xo&aacute;y.</li>\r\n</ul>', NULL, NULL, 0, '2020-04-25 14:52:39', '2020-04-25 15:02:33');
INSERT INTO `products` VALUES (84, 'Thớt gỗ', 'thot-go', 'uploads/product/1587827092_thớt gỗ.jpg', 10, 120000, 100000, 19, 1, 1, 0, 54, NULL, '1', 21, 13, NULL, '<p>THỚT sử dụng chất liệu gỗ cao cấp v&agrave; được sản xuất theo d&acirc;y chuyền c&ocirc;ng nghệ hiện đại, trải qua quy tr&igrave;nh 10 bước tẩm xấy.</p>\r\n\r\n<p>Thớt c&oacute; độ bền cao, chống mối mọt, chống rạn nứt. Bề mặt thớt được gia c&ocirc;ng cẩn thận, bằng phẳng v&agrave; kh&ocirc;ng c&oacute; kẽ hở, hạn chế thực phẩm kh&ocirc;ng dắt v&agrave;o, giữ an to&agrave;n thực phẩm cho người sử dụng. An to&agrave;n cho sức khỏe người d&ugrave;ng.&nbsp;</p>\r\n\r\n<p>Trải qua quy tr&igrave;nh gồm 10 bước tẩm sấy cẩn thận để đảm bảo độ tinh sạch của sản phẩm, kh&ocirc;ng mốc, kh&ocirc;ng mọt, kh&ocirc;ng rạn nứt. Đặc biệt, kh&ocirc;ng sử dụng phẩm m&agrave;u để sơn l&ecirc;n bề mặt thớt n&ecirc;n đảm bảo an to&agrave;n khi sử dụng.</p>\r\n\r\n<p>Vệ sinh dễ d&agrave;ng, sau khi th&aacute;i thịt c&aacute;, rau củ, bạn chỉ cần rửa thớt bằng x&agrave; ph&ograve;ng v&agrave; treo cho r&aacute;o nước để thớt kh&ocirc;ng bị ẩm thấp, dễ dẫn đến vi khuẩn sinh s&ocirc;i.</p>\r\n\r\n<p>Kiểu d&aacute;ng đẹp mắt v&agrave; tiện lợi : Thớt h&igrave;nh vu&ocirc;ng, th&iacute;ch hợp cho c&ocirc;ng việc nội trợ h&agrave;ng ng&agrave;y. Thớt c&oacute; lỗ tr&ograve;n để bạn c&oacute; thể thể treo l&ecirc;n dễ d&agrave;ng.</p>\r\n\r\n<p>K&iacute;ch thước: 340x240x18 mm</p>', NULL, NULL, 0, '2020-04-25 15:04:52', '2020-04-25 15:06:06');
INSERT INTO `products` VALUES (85, 'Túi đựng rác tự phân huỷ (20 cái)', 'tui-dung-rac-tu-phan-huy-20-cai', 'uploads/product/1587828225_túi đựng rác.jpg', 10, 35000, 22000, 20, 1, 1, 0, 51, NULL, NULL, 21, 13, NULL, '<p>Sản phẩm Bao r&aacute;c tự ph&acirc;n huỷ&nbsp;- L&agrave; d&ograve;ng sản phẩm bao r&aacute;c size 3 cuộn m&agrave;u đen</p>\r\n\r\n<p>- Bao r&aacute;c l&agrave; sản phẩm được sử dụng phổ biến trong văn ph&ograve;ng, nh&agrave; xưởng, x&iacute; nghiệp, kho b&atilde;i,....</p>\r\n\r\n<p>- Chất liệu bằng nhựa nynon bền chắc, dẻo dai v&agrave; th&acirc;n thiện với m&ocirc;i trường, c&oacute; c&aacute;c m&agrave;u sắc: xanh dương, đỏ, v&agrave;ng. Kh&ocirc;ng g&acirc;y độc hại cho sức khỏe người sử dụng.</p>\r\n\r\n<p>- L&agrave; sản phẩm sử dụng c&ugrave;ng với c&aacute;c loại th&ugrave;ng r&aacute;c, xọt r&aacute;c, tương ứng với c&aacute;c k&iacute;ch cỡ . C&oacute; thiết kế chắc chắn, chống r&ograve; rỉ v&agrave; k&iacute;n m&ugrave;i. Chất lượng: mẫu m&atilde; đẹp - bền.</p>\r\n\r\n<p>Quy c&aacute;ch: đ&oacute;ng g&oacute;i 3 cuộn, m&agrave;u sắc: đen Xuất xứ : Việt Nam</p>\r\n\r\n<ul>\r\n	<li>Loại tiểu size 44x56cm: 100 _110c&aacute;i/ kg</li>\r\n	<li>Loại trung size 55x65cm: 65_75 c&aacute;i/ kg&nbsp;</li>\r\n	<li>Loại đại size 64x78cm : 48_58 c&aacute;i/ kg&nbsp;</li>\r\n	<li>Loại cực đại size 78x100cm: 30_40 c&aacute;i/kg</li>\r\n</ul>', NULL, NULL, 0, '2020-04-25 15:19:30', '2020-04-25 15:23:56');
INSERT INTO `products` VALUES (86, 'Rọ trồng cây, rau', 'ro-trong-cay-rau', 'uploads/product/1587828375_rọ trồng rau.jpg', 20, 18000, 17000, 21, 1, 1, 0, 51, NULL, NULL, 21, 13, NULL, '<p>RỌ TRỒNG RAU THỦY CANH</p>\r\n\r\n<ul>\r\n	<li>Số lượng: 20 c&aacute;i</li>\r\n	<li>Chất liệu: Nhựa Trắng hoặc Đen</li>\r\n	<li>K&iacute;ch thước rọ: Đường k&iacute;nh ngo&agrave;i mặt tr&ecirc;n 6,5cm- Đường k&iacute;nh mặt trong 6,0cm- Rọ s&acirc;u 6,5cm- Đường k&iacute;nh đ&aacute;y rọ 3,8cm- Lỗ kho&eacute;t đặt rọ 5,5-6,0cm</li>\r\n	<li>Ph&ugrave; hợp trồng rau ăn l&aacute;. Thích hợp với ống nhựa phi 76-phi 90,d&ugrave;ng thủy canh hồi lưu hoặc khay nhựa, nắp xốp l&agrave;m thủy canh tĩnh.</li>\r\n</ul>', NULL, NULL, 0, '2020-04-25 15:19:35', '2020-04-25 15:26:15');
INSERT INTO `products` VALUES (87, 'Bông tắm xơ mướp', 'bong-tam-xo-muop', 'uploads/product/1587828469_bông tắm xơ mướp.jpg', 10, 35000, 32000, 22, 1, 0, 0, 52, NULL, NULL, 21, 13, NULL, '<p>Để l&agrave;m một b&ocirc;ng tắm tự nhi&ecirc;n kh&ocirc;ng đơn giản chỉ lấy quả xơ mướp cắt ra l&agrave; xong m&agrave; phải qua nhiều kh&acirc;u xử l&yacute; rất cầu kỳ. N&agrave;y nh&eacute;, phải lựa chọn rất kỹ những tr&aacute;i xơ mướp c&oacute; xơ mịn, đẹp, bền dai. Sau đ&oacute; phải c&aacute;n qua lại nhiều lần để l&agrave;m mềm sợi xơ, v&igrave; thế m&agrave; sợi xơ kh&ocirc;ng th&ocirc; r&aacute;p m&agrave; rất mềm với l&agrave;n da. 😊</p>\r\n\r\n<p>Với kết cấu sợi đan xen tự nhi&ecirc;n, xơ mướp c&oacute; t&aacute;c dụng m&aacute;t xa cơ thể nhẹ nh&agrave;ng, loại bỏ tế b&agrave;o chết, th&uacute;c đầy t&aacute;i tạo collagen, gi&uacute;p cho l&agrave;n da sạch mịn đầy sức sống. 😎</p>\r\n\r\n<p>C&aacute;ch d&ugrave;ng: thấm nước trước khi sử dụng. D&ugrave;ng k&egrave;m theo sữa tắm. Treo nơi tho&aacute;ng m&aacute;t sau mỗi lần sử dụng. N&ecirc;n thay sau khoảng 1 th&aacute;ng để tr&aacute;nh sinh khuẩn.😀</p>', NULL, NULL, 0, '2020-04-25 15:19:39', '2020-04-25 15:27:49');
INSERT INTO `products` VALUES (88, 'Hộp đựng làm từ bã mía (10 hộp)', 'hop-dung-lam-tu-ba-mia-10-hop', 'uploads/product/1587828659_hộp đựng bã mía.jpg', 100, 23000, 21000, 23, 1, 0, 0, 54, NULL, NULL, 21, 13, NULL, '<p>Hộp l&agrave;m từ 100% bột giấy từ b&atilde; m&iacute;a, một phế phẩm trong qu&aacute; tr&igrave;nh sản xuất đường.</p>\r\n\r\n<ul>\r\n	<li>Dung t&iacute;ch hộp: 600ml</li>\r\n	<li>K&iacute;ch thước(mm): 271 x 182 x 20 (độ s&acirc;u của nắp)/44(độ s&acirc;u của th&acirc;n hộp)</li>\r\n</ul>\r\n\r\n<p>Ph&ugrave; hợp cho qu&aacute;n cơm, nh&agrave; h&agrave;ng, khu du lịch.</p>', NULL, NULL, 0, '2020-04-25 15:19:43', '2020-04-25 15:30:59');
INSERT INTO `products` VALUES (89, 'Bột giặt hữu cơ từ dầu dừa', 'bot-giat-huu-co-tu-dau-dua', 'uploads/product/1587828927_bột giặt.jpg', 10, 157000, 123000, 24, 1, 1, 0, 51, NULL, NULL, 25, 12, NULL, '<p>Bột giặt c&oacute;&nbsp;th&agrave;nh phần ch&iacute;nh l&agrave; l&agrave;nh t&iacute;nh từ dầu dừa, kh&ocirc;ng chứa h&oacute;a chất độc hại. Th&ocirc;ng thường, đa phần c&aacute;c sản phẩm bột giặt hay chất tẩy rửa quần &aacute;o tr&ecirc;n thị trường đều c&oacute; chứa c&aacute;c chất g&acirc;y hại như SLS, Phosphate, Sulfate hay dầu cọ - những chất n&agrave;y sau khi thải ra m&ocirc;i trường hay đại dương c&oacute; thể l&agrave;m hại đến c&aacute;c sinh vật biển, một số sản phẩm thậm ch&iacute; c&ograve;n thử nghiệm tr&ecirc;n cả động vật nữa.</p>\r\n\r\n<p>Gi&aacute;:</p>\r\n\r\n<ul>\r\n	<li>&nbsp;Loại kh&ocirc;ng hương - 175,000 VND/200 gr.</li>\r\n	<li>M&ugrave;i oải hương - 250,000 VND/ 200 gr, Refill 980 VND/ grC&aacute;ch d&ugrave;ng:</li>\r\n</ul>\r\n\r\n<p>C&aacute;ch d&ugrave;ng: Pha 5 gr bột giặt với 200 ml nước cho giặt tay. Pha 10 gr bột giặt với 200 ml nước cho giặt m&aacute;y.</p>', NULL, NULL, 0, '2020-04-25 15:19:46', '2020-04-25 15:35:27');
INSERT INTO `products` VALUES (90, 'Nước lau sàn hữu cơ', 'nuoc-lau-san-huu-co', 'uploads/product/1587829475_nước lau sàn.jpg', 10, 140000, 129000, 25, 1, 0, 0, 51, NULL, NULL, 0, 13, NULL, '<p>Nước lau s&agrave;n hữu cơ&nbsp;LAYER CLEAN: Kh&ocirc;ng sử dụng chất l&agrave;m đặc v&agrave; phẩm m&agrave;u h&oacute;a học. Hương OCEAN BREEZE l&agrave; sự kết hợp của tinh dầu hoa nh&agrave;i, lan dạ hương, hoa hồng, cẩm chướng, thủy ti&ecirc;n v&agrave; tinh dầu quả qu&yacute;t, tạo kh&ocirc;ng gian thơm nhẹ nh&agrave;ng, thư gi&atilde;n. Cơ chế l&agrave;m sạch bằng m&agrave;ng mỏng Protein, ngăn kh&ocirc;ng cho bụi bặm v&agrave;o s&agrave;n, đặc biệt sẽ thấm v&agrave;o thớ gỗ, gi&uacute;p bảo vệ v&agrave; kh&ocirc;ng l&agrave;m s&agrave;n gỗ c&ocirc;ng v&ecirc;nh.</p>', NULL, NULL, 0, '2020-04-25 15:41:58', '2020-04-25 15:44:35');
INSERT INTO `products` VALUES (91, 'Nước Rửa Chén Sinh Học', 'nuoc-rua-chen-sinh-hoc', 'uploads/product/1587829562_nước rửa chén.jpg', 10, 55000, 51000, 26, 1, 0, 0, 54, NULL, NULL, 0, 13, NULL, '<p>Nước rửa ch&eacute;n sinh học One4One được ph&aacute;t triển v&agrave; ho&agrave;n thiện dựa tr&ecirc;n phương ph&aacute;p l&agrave;m l&agrave;m chất tẩy rửa từ enzyme r&aacute;c nh&agrave; bếp (garbage enzyme).</p>\r\n\r\n<p>Th&agrave;nh phần sản phẩm ho&agrave;n to&agrave;n từ tự nhi&ecirc;n như rau, củ, quả, hoa... n&ecirc;n ho&agrave;n to&agrave;n an to&agrave;n với l&agrave;n da của người sử dụng v&agrave; th&acirc;n thiện với m&ocirc;i trường. Dự &aacute;n n&agrave;y&nbsp;gi&uacute;p giải quyết được một lượng rau củ quả bị bỏ ph&iacute; hằng ng&agrave;y, g&oacute;p phần l&agrave;m sạch m&ocirc;i trường sống. Bạn cũng c&oacute; thể t&igrave;m hiểu th&ecirc;m về dự &aacute;n &yacute; nghĩa n&agrave;y nh&eacute; ^^</p>\r\n\r\n<p>Th&agrave;nh phần: Rau, củ v&agrave; hoa tự nhi&ecirc;n đ&atilde; qua sử dụng, phụ gia thực phẩm v&agrave; nước.</p>\r\n\r\n<p>C&ocirc;ng dụng: Rửa ch&eacute;n b&aacute;t, l&agrave;m sạch bề mặt, tẩy sạch c&aacute;c vết dầu mỡ, vết b&aacute;m cứng đầu, khử m&ugrave;i hiệu quả, giảm thiểu nghẹt cống sinh hoạt.</p>\r\n\r\n<p>C&aacute;ch sử dụng: H&ograve;a tan sản phẩm v&agrave;o lượng nước vừa đủ, tạo bọt nhẹ nh&agrave;ng, d&ugrave;ng để rửa ch&eacute;n b&aacute;t. Đối với bề mặt bếp, c&oacute; thể nhỏ lượng nhỏ trực tiếp l&ecirc;n, d&ugrave;ng khăn lau một lần kh&ocirc;ng cần lau lại với nước.</p>\r\n\r\n<p>Hạn sử dụng: 36 th&aacute;ng.</p>', NULL, NULL, 0, '2020-04-25 15:42:00', '2020-04-25 15:46:02');
INSERT INTO `products` VALUES (92, 'Băng Keo Giấy', 'bang-keo-giay', 'uploads/product/1587907680_băng keo.jpg', 10, 19000, 15000, 27, 1, 1, 0, 52, 'https://www.laidayrefill.com/product-page/băng-keo-giấy-lớn-paper-tape-big-4-7cm-x-45m', '1', 19, 12, NULL, '<p>Trong khi băng keo nhựa thường được l&agrave;m bằng m&agrave;ng BOPP trong từ hạt nhựa PP v&agrave; được c&aacute;n th&agrave;nh m&agrave;ng nhựa mỏng, mềm v&agrave; dai, thì băng keo gi&acirc;́y đã sử dụng gi&acirc;́y kraft, một loại giấy th&ocirc; kh&ocirc;ng qua tẩy m&agrave;u đ&ecirc;̉ làm lớp n&ecirc;̀n. Sau đ&oacute; lớp keo nước sẽ được phun l&ecirc;n bề mặt gi&acirc;́y hay nhựa để tạo th&agrave;nh chất kết d&iacute;nh được l&agrave;m từ acrylate copolymer, nhựa th&ocirc;ng hydro h&oacute;a, BOP, DO,...</p>\r\n\r\n<p>K&iacute;ch thước: 1.6cm *45m / 4.7cm *45m</p>', NULL, NULL, 0, '2020-04-25 15:42:02', '2020-04-26 13:28:00');
INSERT INTO `products` VALUES (93, 'Khay gỗ đựng trứng', 'khay-go-dung-trung', 'uploads/product/1587908172_khay trứng.jpg', 10, 125000, 12000, 28, 1, 1, 0, 54, 'https://www.laidayrefill.com/product-page/khay-gỗ-đựng-trứng', '1', 19, 12, NULL, '<p>KHAY ĐỰNG TRỨNG BẰNG GỖ</p>\r\n\r\n<p>Chiếc khay trứng bằng gỗ l&agrave; một phụ kiện tuyệt vời đi k&egrave;m với những quả trứng nhiều m&agrave;u sắc hoặc được d&ugrave;ng để giữ những quả trứng đ&atilde; được luộc để phục vụ cho bữa ăn nhưng chủ yếu l&agrave; để gi&uacute;p giữ những quả trứng cho ch&uacute;ng đừng lăn lung tung. Khay trứng được l&agrave;m bằng gỗ tần b&igrave;&nbsp;với vẻ ngo&agrave;i tinh tế v&agrave; thanh lịch. Ngo&agrave;i ra, bạn cũng c&oacute; thể linh hoạt d&ugrave;ng khay n&agrave;y đựng c&aacute;c loại phụ kiện nữ trang, đ&aacute; trang tr&iacute; xinh xinh tr&ecirc;n b&agrave;n phấn, hay d&ugrave;ng để đựng ch&igrave;a kho&aacute; c&aacute;c loại ở lối ra v&agrave;o để kh&ocirc;ng qu&ecirc;n hay rơi đồ be b&eacute;, hay d&ugrave;ng đựng gia vị, mứt hay c&aacute;c loại hoa tr&agrave; kh&ocirc; trong c&aacute;c bữa tiệc tr&agrave;&hellip;</p>\r\n\r\n<p>Khay c&oacute; thể giữ được 10 quả trứng.</p>\r\n\r\n<p>K&iacute;ch thước: 25 x 12 cm.</p>\r\n\r\n<p>Chất liệu: gỗ tần b&igrave;.</p>\r\n\r\n<p>Xuất xứ: Việt Nam.</p>\r\n\r\n<p>C&Aacute;CH BẢO QUẢN SẢN PHẨM: Rửa với nước v&agrave; x&agrave; ph&ograve;ng rửa ch&eacute;n b&igrave;nh thường. Sau khi vệ sinh sản phẩm n&ecirc;n treo hoặc phơi cho r&aacute;o nước. Kh&ocirc;ng được ng&acirc;m sản phẩm trong nước qu&aacute; l&acirc;u, nước sẽ thấm v&agrave;o v&agrave; g&acirc;y ẩm mốc. C&oacute; thể b&ocirc;i dầu olive để gi&uacute;p m&agrave;u gỗ s&aacute;ng b&oacute;ng sau 1 thời gian sử dụng.</p>', NULL, NULL, 0, '2020-04-25 15:42:04', '2020-04-26 13:36:12');
INSERT INTO `products` VALUES (94, 'Hộp đựng khăn giấy', 'hop-dung-khan-giay', 'uploads/product/1587909141_hộp đựng giấy.jpg', 10, 128000, 124000, 29, 1, 1, 0, 51, 'https://www.laidayrefill.com/product-page/hộp-khăn-giấy-gỗ', '1', 19, 12, NULL, '<p>Nơi sản xuất: Việt Nam.</p>\r\n\r\n<p>K&iacute;ch thước:24x12x6cm.</p>\r\n\r\n<p>Th&agrave;nh phần, vật liệu: Gỗ dẻ gai (beech).</p>\r\n\r\n<p>Đặc điểm:</p>\r\n\r\n<ul>\r\n	<li>M&agrave;u gỗ tự nhi&ecirc;n, thiết kế tối giản sẽ khiến hộp khăn giấy gỗ điểm xuyết kh&ocirc;ng gian trong nh&agrave; như một m&oacute;n đồ thanh lịch v&agrave; nền n&atilde;.</li>\r\n	<li>Kiểu d&aacute;ng đơn giản cũng sẽ gi&uacute;p dễ d&agrave;ng vệ sinh lau ch&ugrave;i hơn.</li>\r\n</ul>\r\n\r\n<p>Hộp d&ugrave;ng để đựng khăn giấy hay bạn cũng c&oacute; thể thay lu&ocirc;n khăn giấy bằng khăn vải xếp lại sau mỗi lần giặt sạch sẽ để c&oacute; thể r&uacute;t như khăn giấy nh&eacute;.</p>', NULL, NULL, 0, '2020-04-26 13:39:58', '2020-04-26 13:52:21');
INSERT INTO `products` VALUES (95, 'Dầu gội thiên nhiên hương bưởi', 'dau-goi-thien-nhien-huong-buoi', 'uploads/product/1587910056_dầu gội thiên nhiên.jpg', 10, 110000, 100000, 30, 1, 1, 0, 52, 'https://www.laidayrefill.com/product-page/dầu-gội-hương-bưởi-thiên-nhiên-pomelo-natural-shampoo', '1', 19, 12, NULL, '<p>DẦU GỘI ĐẦU HƯƠNG BƯỞI THI&Ecirc;N NHI&Ecirc;N</p>\r\n\r\n<p>Th&agrave;nh phần ch&iacute;nh: Nước cất, Vỏ Bưởi, Nước Nha Đam, Chất giữ ẩm từ Dầu Cọ, Chất hoạt động bề mặt từ Dầu Dừa, Vitamin B5, Chất tạo bọt từ dầu dừa, Chất tạo đặc từ bắp, Tinh chất Cam Thảo, Tinh dầu Bưởi, Dầu Thầu Dầu.</p>\r\n\r\n<p>C&ocirc;ng dụng:</p>\r\n\r\n<ul>\r\n	<li>L&agrave;m sạch t&oacute;c, k&iacute;ch th&iacute;ch mọc t&oacute;c, hỗ trợ điều trị rụng t&oacute;c, giảm g&atilde;y rụng t&oacute;c, nu&ocirc;i t&oacute;c chắc khỏe.</li>\r\n	<li>Th&uacute;c đẩy qu&aacute; tr&igrave;nh t&aacute;i tạo to&agrave;n bộ cấu tr&uacute;c ch&acirc;n t&oacute;c từ b&ecirc;n trong v&agrave; nu&ocirc;i dưỡng tế b&agrave;o nang t&oacute;c khỏe hơn.</li>\r\n	<li>Giảm t&igrave;nh trạng ngứa, sạch b&atilde; nhờn da đầu, hỗ trợ giảm t&igrave;nh trạng mảng g&agrave;u ở da đầu.</li>\r\n	<li>Dưỡng t&oacute;c mềm mướt,nu&ocirc;i t&oacute;c d&agrave;y, hạn chế t&igrave;nh trạng t&oacute;c xơ rối, chẻ ngọn.</li>\r\n</ul>\r\n\r\n<p>Hướng dẫn sử dụng:</p>\r\n\r\n<ul>\r\n	<li>L&agrave;m ướt t&oacute;c, lấy một lượng dầu gội vừa đủ, tạo bọt massage nhẹ nh&agrave;ng tr&ecirc;n da đầu.</li>\r\n	<li>Sau khi gội xong, lưu &yacute; n&ecirc;n thấm hết nước, hong kh&ocirc; t&oacute;c, tr&aacute;nh để hầm b&iacute; t&oacute;c sẽ khiến dễ sản sinh g&agrave;u ngứa kh&oacute; chịu.</li>\r\n	<li>N&ecirc;n gội đầu đều đặn từ 2 - 3 lần/ tuần để t&oacute;c lu&ocirc;n được chăm s&oacute;c một c&aacute;ch tốt nhất, gi&uacute;p t&oacute;c lu&ocirc;n sạch khỏe.</li>\r\n	<li>Điểm đặc biệt: Cải thiện, hồi phục m&aacute;i t&oacute;c sau 14 ng&agrave;y sử dụng Th&agrave;nh phần 100% thi&ecirc;n nhi&ecirc;n, tinh dầu nguy&ecirc;n chất ho&agrave;n to&agrave;n kh&ocirc;ng chứa Paraben &amp; h&oacute;a chất độc hại.</li>\r\n</ul>\r\n\r\n<p>Cảnh b&aacute;o an to&agrave;n: An to&agrave;n v&agrave; l&agrave;nh t&iacute;nh cho phụ nữ mang thai v&agrave; trẻ em từ 6 tuổi. Tr&aacute;nh tiếp x&uacute;c trực tiếp với Mắt</p>\r\n\r\n<p>C&aacute;ch Bảo Quản: Bảo quản sản phẩm nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p>Hạn sử dụng: 1 năm kể từ ng&agrave;y sản xuất in tr&ecirc;n bao b&igrave;.</p>', NULL, NULL, 0, '2020-04-26 13:40:02', '2020-04-26 14:07:36');
INSERT INTO `products` VALUES (96, 'Sữa tắm thiên nhiên', 'sua-tam-thien-nhien', 'uploads/product/1587910378_sữa tắm thiên nhiên.jpg', 10, 135000, 130000, 31, 1, 1, 0, 52, 'https://www.laidayrefill.com/product-page/sữa-tắm-oải-hương-thiên-nhiên-lavender-natural-shower-gel', '1', 19, 12, NULL, '<p>Sữa tắm Oải Hương Thi&ecirc;n Nhi&ecirc;n</p>\r\n\r\n<p>Th&agrave;nh phần ch&iacute;nh: Dầu Oliu, Dầu Dừa, Dầu Hạt Cải, Dầu Jojoba, Glycerin tự nhi&ecirc;n, Tinh dầu nguy&ecirc;n chất Lavender</p>\r\n\r\n<p>C&ocirc;ng dụng: L&agrave;m sạch dịu nhẹ cho cơ thể v&agrave; cung cấp độ ẩm cần thiết cho l&agrave;n da.</p>\r\n\r\n<p>Hướng dẫn sử dụng: Lấy một lượng gel tắm vừa đủ cho l&ecirc;n bọt biển hoặc khăn rửa mặt , thoa l&ecirc;n da nhẹ nh&agrave;ng l&agrave;m sạch cơ thể. Tắm sạch bằng nước v&agrave; lau kh&ocirc; người bằng khăn tắm.</p>\r\n\r\n<p>Đặc điểm:</p>\r\n\r\n<ul>\r\n	<li>Th&agrave;nh phần 100% thi&ecirc;n nhi&ecirc;n, tinh dầu nguy&ecirc;n chất l&agrave;nh t&iacute;nh.</li>\r\n	<li>Ho&agrave;n to&agrave;n kh&ocirc;ng chứa Paraben, kh&ocirc;ng chất bảo quản, kh&ocirc;ng chất hoạt động bề mặt v&agrave; kh&ocirc;ng d&ugrave;ng h&oacute;a chất hương tạo m&ugrave;i.</li>\r\n</ul>\r\n\r\n<p>Cảnh b&aacute;o an to&agrave;n:</p>\r\n\r\n<ul>\r\n	<li>An to&agrave;n v&agrave; l&agrave;nh t&iacute;nh cho phụ nữ mang thai v&agrave; trẻ em từ 3 tuổi.</li>\r\n	<li>Tr&aacute;nh tiếp x&uacute;c trực tiếp với Mắt.&nbsp;</li>\r\n</ul>\r\n\r\n<p>C&aacute;ch Bảo Quản: Bảo quản sản phẩm nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p>Hạn sử dụng: 1 năm kể từ ng&agrave;y sản xuất in tr&ecirc;n bao b&igrave;</p>', NULL, NULL, 0, '2020-04-26 13:40:05', '2020-04-26 14:12:58');
INSERT INTO `products` VALUES (97, 'Cọ Rửa Chén Đĩa Tròn EcoCleaner / EcoCleaner Round Dish Brush', 'co-rua-chen-dia-tron-ecocleaner-ecocleaner-round-dish-brush', 'uploads/product/1589557871_1c272d_996f6f850650495b8828815821065379_mv2_d_3000_2999_s_4_2.jpg', 10, 135000, 115000, 67, 1, 0, 0, 54, 'https://www.laidayrefill.com/product-page/c%E1%BB%8D-r%E1%BB%ADa-ch%E1%BA%A3o-n%E1%BB%93i-ecocleaner-ecocleaner-pot-brush', 'S027006', 19, 12, NULL, NULL, NULL, NULL, 0, '2020-05-15 15:51:11', '2020-05-15 15:51:11');
INSERT INTO `products` VALUES (98, 'Refill Xà bông thảo mộc Herbs/ Refill Handmade Soap Herbs', 'refill-xa-bong-thao-moc-herbs-refill-handmade-soap-herbs', 'uploads/product/1589558250_c57a90_90a9ca25187144c4bf75f2e5607f3b6f_mv2_d_3322_4153_s_4_2.jpg', 5, 120000, 99000, 23, 1, 1, 0, 55, NULL, 'abc', 19, 12, NULL, '<pre>\r\n\r\n&nbsp;</pre>\r\n\r\n<p>Xuất xứ/Origin: Vietnam</p>\r\n\r\n<p>Gi&aacute; / Price : 1.100&nbsp;VND /gram</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sản phẩm refill&nbsp;c&oacute; 3&nbsp;loại:&nbsp;</p>\r\n\r\n<p>- X&agrave; ph&ograve;ng TRE &nbsp;- Than tre Th&aacute;i B&igrave;nh</p>\r\n\r\n<p>-&nbsp;X&agrave; ph&ograve;ng TH&Ocirc;NG &nbsp;- Dầu th&ocirc;ng Đ&agrave; Lạt, l&aacute; atiso Đ&agrave; Lạt</p>\r\n\r\n<p>-&nbsp;X&agrave; ph&ograve;ng THỔ &nbsp;- Mật ong U Minh, Nghệ v&agrave;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Y&ecirc;u cầu khi bảo quản x&agrave; ph&ograve;ng</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Bảo quản nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t</p>\r\n	</li>\r\n	<li>\r\n	<p>Nhiệt độ dưới 28 độ C&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng để nơi ẩm mốc hoặc c&oacute; d&iacute;nh nước&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng bỏ v&agrave;o hộp k&iacute;n v&agrave; kh&ocirc;ng tho&aacute;ng kh&iacute;</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng để nơi c&oacute; &aacute;nh nắng trực tiếp</p>\r\n	</li>\r\n	<li>\r\n	<p>Đối với x&agrave; ph&ograve;ng c&oacute; hương thơm dễ bay (Thảo) v&agrave; c&oacute; th&agrave;nh phần nhạy cảm với &aacute;nh s&aacute;ng v&agrave; nhiệt độ (Em), kh&ocirc;ng trưng b&agrave;y sản phẩm b&ecirc;n ngo&agrave;i bao b&igrave;</p>\r\n	</li>\r\n	<li>\r\n	<p>Hạn chế tiếp x&uacute;c trực tiếp bằng tay v&agrave;o x&agrave; ph&ograve;ng nếu kh&ocirc;ng c&oacute; bao b&igrave; bảo quản.</p>\r\n	</li>\r\n</ul>', NULL, NULL, 0, '2020-05-15 15:57:30', '2020-05-18 08:26:43');
INSERT INTO `products` VALUES (99, 'Kem đánh răng than hoạt tính - Refill /Activated Charcoal Toothpaste - per gram', 'kem-danh-rang-than-hoat-tinh-refill-activated-charcoal-toothpaste-per-gram', 'uploads/product/1589558335_danh rang than hoat tinh.jpg', 6, 50000, 45000, 24, 1, 0, 0, 55, 'https://www.laidayrefill.com/product-page/kem-%C4%91%C3%A1nh-r%C4%83ng-than-ho%E1%BA%A1t-t%C3%ADnh-refill-activated-charcoal-toothpaste-per-gram', '123', 19, 12, NULL, NULL, NULL, NULL, 0, '2020-05-15 15:58:55', '2020-05-15 15:58:55');
INSERT INTO `products` VALUES (100, 'REFILL Xà Bông Thảo Mộc / REFILL Herbal Traditional Handmade Soap', 'refill-xa-bong-thao-moc-refill-herbal-traditional-handmade-soap', 'uploads/product/1589558840_xabongthaomoc_herbal.jpg', 7, 20000, 4500, 24, 1, 0, 0, 55, NULL, 'S012040', 19, 12, NULL, '<pre>\r\n\r\n&nbsp;</pre>\r\n\r\n<p>Origin: Vietnam&nbsp;</p>\r\n\r\n<p>Price: 450đ/1 gram</p>\r\n\r\n<p><strong>1- X&agrave; b&ocirc;ng thảo mộc : Hoa hồng</strong></p>\r\n\r\n<p>C&aacute;nh hoa hồng chứa chất kh&aacute;ng khuẩn, nhẹ nh&agrave;ng điều trị, giảm sưng tấy c&aacute;c vết mụn tr&ecirc;n da. C&aacute;c vitamin C, c&aacute;c vitamin nh&oacute;m B, D, E, K v&agrave; chất chống oxy h&oacute;a gi&uacute;p thu hẹp lỗ ch&acirc;n l&ocirc;ng nu&ocirc;i dưỡng tận s&acirc;u b&ecirc;n trong cấu tr&uacute;c, duy tr&igrave; độ ẩm cho da lu&ocirc;n mịn m&agrave;ng v&agrave; tươi trẻ.</p>\r\n\r\n<p><strong>2- X&agrave; b&ocirc;ng thảo mộc: Bạc h&agrave;</strong></p>\r\n\r\n<p>Dầu thực vật trong b&aacute;nh x&agrave; ph&ograve;ng chứa nhiều vitamin E v&agrave; c&aacute;c axit b&eacute;o b&atilde;o h&ograve;a, nhẹ nh&agrave;ng cuốn đi bụi bẩn, cung cấp độ ẩm cho da, gi&uacute;p s&aacute;t khuẩn, kiểm so&aacute;t tuyến b&atilde; nhờn, cho bạn l&agrave;n da mịn m&agrave;ng, kh&ocirc;ng c&ograve;n mụn. Hương bạc h&agrave; thoang thoảng xua tan căng thẳng, stress v&agrave; gi&uacute;p tinh thần bạn th&ecirc;m phấn chấn.</p>\r\n\r\n<p><strong>3- X&agrave; b&ocirc;ng thảo mộc: Sả</strong></p>\r\n\r\n<p>Tinh dầu được chiết xuất từ sả c&oacute; khả năng ti&ecirc;u khử v&agrave; ngăn chặn sự ph&aacute;t triển của nấm, vi khuẩn tr&ecirc;n da, từ đ&oacute; khử m&ugrave;i h&ocirc;i, lưu hương thơm dễ chịu suốt cả ng&agrave;y. Chất l&agrave;m se c&oacute; trong sả gi&uacute;p thu kh&iacute;t lỗ ch&acirc;n l&ocirc;ng, hạn chế tiết dầu gi&uacute;p c&acirc;n bằng da nhờn, chống đổ mồ h&ocirc;i qu&aacute; mức. Đối với người c&oacute; thể trạng đang yếu do bị cảm lạnh, cảm c&uacute;m hoặc nhiễm khuẩn nhẹ, tắm với nước ấm v&agrave; x&agrave; ph&ograve;ng sả l&agrave; c&aacute;ch tốt nhất để mau hồi phục sức khỏe.</p>\r\n\r\n<p><strong>4- X&agrave; b&ocirc;ng thảo mộc: Socola</strong></p>\r\n\r\n<p>X&agrave; ph&ograve;ng chocolate khả năng t&aacute;i tạo tế b&agrave;o mới, chống l&atilde;o h&oacute;a, tăng cường độ đ&agrave;n hồi, l&agrave;m mờ vết th&acirc;m n&aacute;m hữu hiệu nhất, chống lại tia UV, tr&aacute;nh bị ch&aacute;y nắng v&agrave; ngăn ngừa nguy cơ ung thư da.&nbsp;Những hạt cacao c&ograve;n gi&uacute;p tăng cường lưu th&ocirc;ng m&aacute;u, mang dưỡng chất đến c&aacute;c m&ocirc; tế b&agrave;o nu&ocirc;i da, gi&uacute;p giảm thiểu hormone g&acirc;y stress, mang lại tinh thần thư th&aacute;i nếu bạn tắm x&agrave; ph&ograve;ng s&ocirc;c&ocirc;la mỗi ng&agrave;y đấy.</p>\r\n\r\n<p><strong>5- X&agrave; b&ocirc;ng thảo mộc: Gạo</strong></p>\r\n\r\n<p>C&ocirc;ng thức kết hợp sữa gạo với hỗn hợp dầu thực vật sẽ lấy đi lớp tế b&agrave;o chết v&agrave; bụi bẩn, cho l&agrave;n da th&ocirc;ng tho&aacute;ng, tạo điều kiện cho sữa gạo dễ d&agrave;ng mang dưỡng chất thấm s&acirc;u v&agrave;o b&ecirc;n trong v&agrave; cung cấp nhiều vitamin, nhất l&agrave; vitamin nh&oacute;m B, k&iacute;ch th&iacute;ch tế b&agrave;o tăng cường sản xuất collagen, gi&uacute;p cải thiện độ đ&agrave;n hồi, c&acirc;n bằng t&ocirc;ng m&agrave;u da.&nbsp;</p>\r\n\r\n<p><strong>6- X&agrave; b&ocirc;ng thảo mộc: C&agrave; ph&ecirc;</strong></p>\r\n\r\n<p>Với th&agrave;nh phần caffein nổi trội b&ecirc;n trong hạt c&agrave; ph&ecirc;, x&agrave; b&ocirc;ng c&agrave; ph&ecirc; c&oacute; t&aacute;c dụng đốt ch&aacute;y mỡ, l&agrave;m da th&ecirc;m săn chắc v&agrave; c&aacute;c axit nicotinic c&oacute; khả năng ph&ograve;ng ngừa bệnh nứt da, chữa vi&ecirc;m da. Những hạt c&agrave; ph&ecirc; mịn lẫn trong b&aacute;nh x&agrave; ph&ograve;ng se nhẹ nh&agrave;ng tẩy đi lớp tế b&agrave;o chết chai sần tr&ecirc;n da, kh&aacute;ng vi&ecirc;m v&agrave; chống oxy h&oacute;a, gi&uacute;p nu&ocirc;i dưỡng l&agrave;n da s&aacute;ng đều m&agrave;u sau một thời gian ngắn sử dụng.</p>\r\n\r\n<p><strong>7- X&agrave; b&ocirc;ng thảo mộc: Quế</strong></p>\r\n\r\n<p>X&agrave; b&ocirc;ng quế gi&uacute;p tẩy tế b&agrave;o chết dịu nhẹ, diệt khuẩn v&agrave; ngăn ngừa l&atilde;o h&oacute;a cho l&agrave;n da cũng như gi&uacute;p loại trừ sự căng thẳng thần kinh, giảm đau đầu v&agrave; k&iacute;ch th&iacute;ch n&atilde;o nhạy b&eacute;n hơn.</p>\r\n\r\n<p><strong>8- X&agrave; b&ocirc;ng thảo mộc: Tr&agrave; đen</strong></p>\r\n\r\n<p>Nhờ chứa nhiều c&aacute;c hợp chất chống oxy h&oacute;a v&agrave; c&aacute;c vitamin, tr&agrave; đen gi&uacute;p bổ sung vitamin v&agrave; kho&aacute;ng chất gi&uacute;p da trắng mịn tươi trẻ, tăng cường lưu th&ocirc;ng m&aacute;u dưới da, mang lại l&agrave;n da hồng h&agrave;o tr&ocirc;ng đầy sức sống. Ch&iacute;nh nhờ hiện tượng l&ecirc;n men m&agrave; tr&agrave; đen c&oacute; m&ugrave;i thơm đặc biệt v&agrave; c&oacute; m&agrave;u sậm chứa nhiều hoạt chất c&oacute; t&aacute;c dụng kh&aacute;ng oxy h&oacute;a, chống t&igrave;nh trạng l&atilde;o h&oacute;a tế b&agrave;o, chống nhờn v&agrave; bổ sung chất dinh dưỡng thực vật cho cơ thể.</p>\r\n\r\n<p><strong>9- X&agrave; b&ocirc;ng thảo mộc: Tr&aacute;i nh&agrave;u</strong></p>\r\n\r\n<p>X&agrave; b&ocirc;ng tr&aacute;i nh&agrave;u l&agrave;m từ dầu dừa nguy&ecirc;n chất kết hợp với tinh chất tr&aacute;i nh&agrave;u tươi gi&uacute;p bạn chăm s&oacute;c da v&agrave; tẩy tế b&agrave;o chết cho l&agrave;n da, tẩy trắng da tự nhi&ecirc;n một c&aacute;ch nhanh nhất, c&oacute; t&aacute;c dụng chống nắng, chống l&atilde;o h&oacute;a tế b&agrave;o da v&agrave; l&agrave;m trẻ h&oacute;a l&agrave;n da cho bạn.</p>\r\n\r\n<p><strong>10- X&agrave; b&ocirc;ng thảo mộc: Nghệ</strong></p>\r\n\r\n<p>Với một lượng chất chống oxy ho&aacute; lớn trong nghệ, x&agrave; b&ocirc;ng nghệ sẽ l&agrave;m một m&oacute;n qu&agrave; tuyệt vời để ngăn ngừa l&atilde;o ho&aacute;, gi&uacute;p dưỡng trắng n&otilde;n n&agrave;, giảm vết th&acirc;m n&aacute;m, ngăn ngừa v&agrave; chữa l&agrave;nh mụn &ndash; sẹo. Đặc biệt, x&agrave; b&ocirc;ng nghệ th&iacute;ch hợp d&ugrave;ng cho những người đang bệnh cảm lạnh, bệnh thủy đậu, ch&agrave;m, dị ứng hoặc c&oacute; những vết thương hở tr&ecirc;n da.</p>\r\n\r\n<p><strong>11- X&agrave; b&ocirc;ng thảo mộc: Mật ong</strong></p>\r\n\r\n<p>Với ba t&iacute;nh năng nổi bật s&aacute;t khuẩn, dưỡng ẩm v&agrave; chống l&atilde;o h&oacute;a, x&agrave; b&ocirc;ng mật ong sẽ gi&uacute;p bạn c&oacute; một l&agrave;n da tươi tắn, căn mịn, chữa l&agrave;nh c&aacute;c vết thương tr&ecirc;n gia v&agrave; ngăn ngừa v&agrave; gi&uacute;p điều trị mụn hiệu quả.</p>\r\n\r\n<p><strong>12- X&agrave; b&ocirc;ng thảo mộc: Tr&agrave; xanh</strong></p>\r\n\r\n<p>B&eacute; x&agrave; ph&ograve;ng chứa tinh dầu bạc h&agrave; n&agrave;y sẽ gi&uacute;p s&aacute;t khuẩn, l&agrave;m m&aacute;t sạch l&agrave;n da của bạn hiệu quả, cũng như kiểm so&aacute;t tuyến b&atilde; nhờn, đem đến cho bạn một l&agrave;n da mịn m&agrave;ng, kh&ocirc;ng c&ograve;n mụn. Ch&iacute;nh hương bạc h&agrave; thoang thoảng sẽ xua tan đi những căng thẳng, stress v&agrave; tinh thần tỉnh tảo để lại c&oacute; những ng&agrave;y l&agrave;m việc hiệu quả đấy.</p>\r\n\r\n<p><strong>13- X&agrave; b&ocirc;ng thảo mộc: Bia</strong></p>\r\n\r\n<p>Với th&agrave;nh phần l&agrave; men bia, vitamin nh&oacute;m B v&agrave; rất nhiều chất c&oacute; lợi kh&aacute;c, x&agrave; ph&ograve;ng bia c&oacute; t&aacute;c dụng t&iacute;ch cực gi&uacute;p l&agrave;m ẩm v&agrave; phục hồi những vấn đề của da như&nbsp;ti&ecirc;u diệt&rdquo; những vi khuẩn g&acirc;y mụn trứng c&aacute;, l&agrave;m chậm sản xuất b&atilde; nhờn, điều trị v&agrave; giảm thiểu t&igrave;nh trạng mụn trứng c&aacute;&nbsp;hay th&acirc;̣m chí là bạn kh&ocirc;ng c&acirc;̀n phải dùng đ&ecirc;́n nước hoa h&ocirc;̀ng hay sữa rửa mặt vì chính xà b&ocirc;ng bia sẽ giúp làm sạch và se khít l&ocirc;̃ ch&acirc;n l&ocirc;ng.&nbsp;</p>\r\n\r\n<p><strong>14- X&agrave; b&ocirc;ng thảo mộc: Rượu vang</strong></p>\r\n\r\n<p>Xà b&ocirc;ng rượu vang cùng&nbsp; những th&agrave;nh phần ch&iacute;nh như flavonoid, resveratrol v&agrave; tannin có khả năng chống oxy h&oacute;a cực cao mang đi những bụi bẩn, tế b&agrave;o da tho&aacute;i h&oacute;a, trả lại l&agrave;n da tươi mới sau một ng&agrave;y mệt mỏi cùng với&nbsp;&nbsp;AHA thi&ecirc;n nhi&ecirc;n có đặc t&iacute;nh khử tr&ugrave;ng v&agrave; chống vi&ecirc;m giúp bạn chống mụn v&agrave; l&agrave;m sạch lỗ ch&acirc;n l&ocirc;ng, kiểm so&aacute;t được da nhờn v&agrave; tắc nghẽn.&nbsp;</p>\r\n\r\n<p><strong>15- X&agrave; b&ocirc;ng thảo mộc: Oải hương</strong></p>\r\n\r\n<p>X&agrave; ph&ograve;ng Oải Hương kết hợp với dầu dừa v&agrave; dầu cọ gi&uacute;p giữ ẩm v&agrave; mang chất dinh dưỡng nu&ocirc;i da từ tận s&acirc;u b&ecirc;n trong đồng thời bảo vệ da ngay tr&ecirc;n bề mặt, cho bạn l&agrave;n da mềm mại, mịn m&agrave;ng v&agrave; thơm nồng n&agrave;n. Hương hoa Lavender thơm nức c&oacute; t&aacute;c dụng thả lỏng tinh thần, l&agrave;m dịu c&aacute;c cơn đau v&agrave; đuổi c&ocirc;n tr&ugrave;ng, hương hoa thơm ng&aacute;t bao quanh lấy bạn khiến mọi lo &acirc;u bỗng dưng tan biến hết.</p>\r\n\r\n<p><strong>16- X&agrave; b&ocirc;ng thảo mộc: Đậu đỏ</strong></p>\r\n\r\n<p>Đậu đỏ được biết đến với khả năng gi&uacute;p l&agrave;n da trắng s&aacute;ng hồng h&agrave;o, khỏe mạnh được kết hợp với dầu dừa sẽ gi&uacute;p tẩy đi lớp tế b&agrave;o da hư tổn v&agrave; chết, dưỡng trắng s&acirc;u từ b&ecirc;n trong, k&iacute;ch th&iacute;ch t&aacute;i tạo m&agrave;u da hồng h&agrave;o v&agrave; duy tr&igrave; sự khỏe mạnh của da. Sự kết hợp n&agrave;y c&ograve;n gi&uacute;p bảo vệ l&agrave;n da của bạn trước t&aacute;c động ti&ecirc;u cực của &aacute;nh nắng mặt trời, kh&aacute;ng khuẩn v&agrave; bảo vệ da khỏi tia UV.</p>\r\n\r\n<p><strong>17. X&agrave; b&ocirc;ng thảo mộc : Sữa d&ecirc; (650đ/gram)</strong></p>\r\n\r\n<p>Dầu dừa, dầu thực vật, dầu olive, bột sữa d&ecirc;, sữa d&ecirc; tươi</p>\r\n\r\n<p>Coconut oil, vegetable oil, olive oil, goat milk powder, fresh goat milk&nbsp;</p>\r\n\r\n<p><strong>18. X&agrave; b&ocirc;ng thảo mộc: Than tre</strong></p>\r\n\r\n<p>Dầu dừa, dầu thực vật, dầu olive,&nbsp;than&nbsp;tre&nbsp;hoạt t&iacute;nh, tinh dầu bạc h&agrave;</p>\r\n\r\n<p>Coconut oil, vegetable oil, olive oil, activated charcoal, mint essential oils</p>\r\n\r\n<p><strong>Bảo quản</strong>: ở nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp, hay bị gặm nhấm</p>\r\n\r\n<p><strong>HSD</strong>: 2 năm kể từ ng&agrave;y sản xuất v&agrave; 6 th&aacute;ng kể từ ng&agrave;y th&aacute;o bao b&igrave;.</p>', NULL, NULL, 0, '2020-05-15 15:59:54', '2020-05-17 14:47:57');
INSERT INTO `products` VALUES (101, 'Hộp thiếc tròn/ Round Tin Box', 'hop-thiec-tron-round-tin-box', 'uploads/product/1589558490_hopthiectron.jpg', 50, 10000, 10000, 25, 1, 0, 0, 55, 'https://www.laidayrefill.com/product-page/h%E1%BB%99p-thi%E1%BA%BFc-tr%C3%B2n-round-tin-box', 'hopthiectron', 19, 12, NULL, NULL, NULL, NULL, 0, '2020-05-15 16:01:30', '2020-05-15 16:01:30');
INSERT INTO `products` VALUES (102, 'Refill Nước Hoa Khô Cao Cấp/ Premium Solid Perfume', 'refill-nuoc-hoa-kho-cao-cap-premium-solid-perfume', 'uploads/product/1589558607_nuoc hoa kho cc.jpg', 15, 300000, 285000, 26, 1, 1, 0, 55, NULL, 'nuochoacc', 19, 12, NULL, '<pre>\r\n\r\n&nbsp;</pre>\r\n\r\n<p>Sizes:12 - 14 gram&nbsp;</p>\r\n\r\n<p>Scents: Velvet, Light As Air, Blackwood&nbsp;</p>\r\n\r\n<p>Origin: Vietnam</p>\r\n\r\n<p>Refill l&agrave;m đầy trở lại khi d&ugrave;ng hết: 285,000 Đ</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>SCENTS &ndash; C&Aacute;C M&Ugrave;I HƯƠNG</p>\r\n\r\n<p>These scent notes above are played throughout the product line which consists of: premium scented candles, solid perfumes, scented sachets.</p>\r\n\r\n<p>C&aacute;c m&ugrave;i hương tr&ecirc;n đưa v&agrave;o xuy&ecirc;n suốt c&aacute;c sản phẩm bao gồm: nến thơm cao cấp, nước hoa kh&ocirc;, t&uacute;i thơm.</p>\r\n\r\n<p>LIGHT AS AIR</p>\r\n\r\n<p>Description:&nbsp;</p>\r\n\r\n<p>Soft, Peaceful, Tranquil</p>\r\n\r\n<p>Nhẹ nh&agrave;ng &ndash; an tĩnh</p>\r\n\r\n<p>Notes:</p>\r\n\r\n<p>Perfume notes: Mint, Apple, Green Leaves, Patchouli</p>\r\n\r\n<p>C&aacute;c nốt hương ch&iacute;nh: bạc h&agrave;, t&aacute;o, l&aacute; xanh, hoắc hương</p>\r\n\r\n<p>VELVET&nbsp;</p>\r\n\r\n<p>Description:</p>\r\n\r\n<p>Rebellious, Deep, Mysterious&nbsp;</p>\r\n\r\n<p>&nbsp; Ph&aacute; c&aacute;ch &ndash; Quyến rũ</p>\r\n\r\n<p>Notes:</p>\r\n\r\n<p>Perfume notes: White flowers, Virginia Cedar&nbsp;</p>\r\n\r\n<p>C&aacute;c nốt hương ch&iacute;nh: hoa trắng, gỗ tuyết t&ugrave;ng Virginia</p>\r\n\r\n<p>BLACKWOOD&nbsp;</p>', NULL, NULL, 0, '2020-05-15 16:03:27', '2020-05-17 14:43:49');
INSERT INTO `products` VALUES (103, 'Lọ Thuỷ Tinh, Vòi Pump - Glass Bottle With Plastic Pump Head, 350ml', 'lo-thuy-tinh-voi-pump-glass-bottle-with-plastic-pump-head-350ml', 'uploads/product/1589558929_lothuytinhvoipump_350ml.jpg', 5, 50000, 40000, 28, 1, 1, 0, 55, 'https://www.laidayrefill.com/product-page/l%E1%BB%8D-thu%E1%BB%B7-tinh-v%C3%B2i-pump-glass-bottle-with-plastic-pump-head-350ml', 'S032058', 19, 12, NULL, NULL, NULL, NULL, 0, '2020-05-15 16:08:49', '2020-05-15 16:08:49');
INSERT INTO `products` VALUES (104, 'Hộp Ống Hút Cỏ Khô (100 cái/hộp)/ Dry Grass Straw Box (100)', 'hop-ong-hut-co-kho-100-caihop-dry-grass-straw-box-100', 'uploads/product/1589685598_tui dung co kho 100c.jpg', 5, 149000, 110000, 30, 1, 1, 0, 56, NULL, NULL, 19, 12, NULL, '<p>BỘ SẢN PHẨM CỎ B&Agrave;NG</p>\r\n\r\n<p>Đ&acirc;y l&agrave; c&acirc;u chuyện thổi sức sống mới cho c&aacute;c sản phẩm đời sống v&agrave; thủ c&ocirc;ng mang đậm chất địa phương của v&ugrave;ng đất ngập nước theo m&ugrave;a ở v&ugrave;ng Đồng Th&aacute;p Mười v&agrave; Tứ gi&aacute;c Long Xuy&ecirc;n - nơi nước c&oacute; m&agrave;u n&acirc;u v&agrave;ng của ph&egrave;n v&agrave; c&aacute; lia thia lẩn khuất dưới c&aacute;nh đồng cỏ b&agrave;ng ​v&agrave; rừng tr&agrave;m gi&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ecogreen&nbsp;s&aacute;t c&aacute;nh c&ugrave;ng c&aacute;c c&ocirc;, c&aacute;c b&agrave;, c&aacute;c chị từ chọn lựa nguy&ecirc;n liệu, cải tiến v&agrave; ph&aacute;t triển sản phẩm, kiểm định chất lượng để đưa ra những sản phẩm c&oacute; độ ho&agrave;n thiện chăm ch&uacute;t như l&agrave;m cho nh&agrave; d&ugrave;ng. V&agrave; khi c&aacute;c c&ocirc;, c&aacute;c b&agrave;, c&aacute;c chị sống được bằng nghề, kiến thức nghề được giữ g&igrave;n v&agrave; truyền dạy lại. Con người, văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n của hệ sinh th&aacute;i đất ph&egrave;n ngập nước nhờ đ&oacute; th&ecirc;m gắn kết​.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&acirc;y cỏ b&agrave;ng, c&ograve;n gọi l&agrave; b&agrave;ng, c&oacute;i b&agrave;ng, c&oacute; danh ph&aacute;p hai phần l&agrave; Lepironia articulata, thuộc chi Lepironia nằm trong họ C&oacute;i (Cyperaceae). B&agrave;ng c&oacute; th&acirc;n dưới (căn h&agrave;nh) cứng, to khoảng 8&ndash;10 mm, nằm ngang trong b&ugrave;n. Th&acirc;n cỏ b&agrave;ng thẳng đứng cao khoảng 1 m, c&oacute; ngấn ngang, đ&aacute;y c&oacute; 3-4 bẹ, bao nhau cao 15&ndash;20 cm. Gi&eacute; hoa ở ch&oacute;t th&acirc;n (tức l&agrave; ngọn) cao khoảng 1,5-2,0 cm v&agrave; rộng đến 1 cm. B&ocirc;ng quả cao 3&ndash;4 mm. V&ograve;i nhụy chẻ hai, trổ b&ocirc;ng quanh năm. Trước kia, cỏ b&agrave;ng được người d&acirc;n đồng bằng s&ocirc;ng Cửu Long thu hoạch để l&agrave;m c&aacute;c sản phẩm thủ c&ocirc;ng mỹ nghệ, như l&agrave; để đan đệm, l&agrave;m n&oacute;n, bao b&igrave;, l&agrave;m n&oacute;p, lợp nh&agrave; tranh. B&acirc;y giờ c&ograve;n được sử dụng l&agrave;m ống h&uacute;t thay thế loại nhựa th&ocirc;ng thường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cỏ b&agrave;ng l&agrave; loại cỏ cao d&agrave;i, th&acirc;n tr&ograve;n rỗng, mọc hoang ở v&ugrave;ng nước ph&egrave;n chua. Cỏ b&agrave;ng khi gi&agrave; được h&aacute;i về phơi kh&ocirc; v&agrave; gi&atilde; bằng ch&agrave;y gỗ. Những cọng cỏ b&agrave;ng l&yacute; tưởng để đan l&aacute;t l&agrave; những cọng cỏ b&agrave;ng nhỏ v&agrave; d&agrave;i trong khi những cọng cỏ to trước đ&acirc;y bị bỏ đi hoặc tận dụng lợp m&aacute;i nh&agrave; th&igrave; nay đ&atilde; được tr&acirc;n trọng v&agrave; ưa th&iacute;ch với c&aacute;i t&ecirc;n Ống H&uacute;t Cỏ b&agrave;ng.</p>', NULL, NULL, 0, '2020-05-17 03:19:58', '2020-05-17 14:34:34');
INSERT INTO `products` VALUES (105, 'Giỏ Đệm Cỏ Bàng/ Lepironia Articulata Grass Basic Bag', 'gio-dem-co-bang-lepironia-articulata-grass-basic-bag', 'uploads/product/1589685665_651a17_27d90add97c14434a3ef8968280890aa_mv2.jpg', 2, 235000, 235000, 31, 1, 0, 0, 56, NULL, '321', 19, 12, NULL, '<pre>\r\n\r\n&nbsp;</pre>\r\n\r\n<p>LEPIRONIA ARTICULATA GRASS BASIC&nbsp;BAG - SIZE S</p>\r\n\r\n<p>GIỎ ĐỆM&nbsp;CỎ B&Agrave;NG - SIZE NHỎ&nbsp;</p>\r\n\r\n<p>Price gi&aacute;: 235,000 đ&nbsp;</p>\r\n\r\n<p>Chất liệu: cỏ b&agrave;ng tự nhi&ecirc;n</p>\r\n\r\n<p>Cỡ sợi b&agrave;ng: cỡ nh&igrave;</p>\r\n\r\n<p>K&iacute;ch thước:&nbsp;d&agrave;i 38cm, cao 34 cm, đ&aacute;y rộng 10cm</p>\r\n\r\n<p>Quai ngắn</p>', NULL, NULL, 0, '2020-05-17 03:21:05', '2020-05-17 14:41:51');
INSERT INTO `products` VALUES (106, 'Bút Bi Tre/ Bamboo Handmade Ballpoint Pen', 'but-bi-tre-bamboo-handmade-ballpoint-pen', 'uploads/product/1589685754_but bi tre.jpg', 2, 69000, 59000, 32, 1, 0, 0, 56, NULL, NULL, 19, 12, NULL, '<pre>\r\n\r\n&nbsp;</pre>\r\n\r\n<p>Xuất xứ: Việt Nam</p>\r\n\r\n<p>Chất liệu: Tre, ruột mực nhựa c&oacute; thể thay thế</p>\r\n\r\n<p>K&iacute;ch thước: d&agrave;i 17 cm</p>\r\n\r\n<p>Trọng lượng: 7 gram</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Được thiết kế với mục đ&iacute;ch phục vụ cho việc ghi ch&eacute;p dễ d&agrave;ng v&agrave; tiện lợi hơn, ch&uacute;ng m&igrave;nh tận dụng sự ph&aacute;t triển nhanh ch&oacute;ng v&agrave; rất th&acirc;n thiện với m&ocirc;i trường của c&acirc;y tre để l&agrave;m ra chiếc b&uacute;t bi n&agrave;y. To&agrave;n bộ sản phẩm được sử dụng kỹ thuật cắt sợi để tạo cảm gi&aacute;c cầm chắc hơn khi viết. Những chiếc b&uacute;t bi tre được thiết kế với sự ph&acirc;n bố trọng lượng ho&agrave;n hảo. Khi viết, trọng t&acirc;m của điểm trọng lực đưa về ph&iacute;a ng&ograve;i b&uacute;t, mang đến cho bạn trải nghiệm viết tốt nhất. Một điểm độc đ&aacute;o kh&aacute;c về thiết kế của chiếc b&uacute;t thủ c&ocirc;ng n&agrave;y khiến ch&uacute;ng kh&ocirc;ng giống như những c&acirc;y b&uacute;t biro tre kh&aacute;c, đ&oacute; ch&iacute;nh l&agrave; chiếc b&uacute;t tre của nh&agrave; Lại Đ&acirc;y n&agrave;y c&oacute; một chiếc kẹp để gi&uacute;p bạn dễ d&agrave;ng gắn v&agrave; giữ ch&uacute;ng b&ecirc;n m&igrave;nh mỗi ng&agrave;y. Chiếc b&uacute;t bi tre n&agrave;y c&ograve;n được thiết kế để sử dụng trong nhiều năm, cho ph&eacute;p bạn thay đổi v&agrave; thay thế ruột mực dễ d&agrave;ng đấy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&Oacute; KHẢ NĂNG PH&Acirc;N HỦY SINH HỌC &amp; TH&Acirc;N THIỆN VỚI M&Ocirc;I TRƯỜNG 100%</p>\r\n\r\n<p>Mỗi chiếc b&uacute;t được l&agrave;m bằng tre từ v&ugrave;ng rừng n&uacute;i quanh khu vực Thanh H&oacute;a, Việt Nam.</p>\r\n\r\n<p>HỮU CƠ V&Agrave; TỰ NHI&Ecirc;N</p>\r\n\r\n<p>Mỗi chiếc b&uacute;t của ch&uacute;ng m&igrave;nh được l&agrave;m thủ c&ocirc;ng v&agrave; được xử l&yacute; bằng dầu cam qu&yacute;t an to&agrave;n thực phẩm. Ch&uacute;ng m&igrave;nh chỉ sử dụng c&acirc;y trưởng th&agrave;nh v&agrave; kh&ocirc;ng sử dụng bất kỳ loại thuốc trừ s&acirc;u nh&acirc;n tạo hoặc bất kỳ</p>\r\n\r\n<p>&nbsp;T&Aacute;I SỬ DỤNG</p>\r\n\r\n<p>Với thiết kế độc đ&aacute;o n&agrave;y, bạn ho&agrave;n to&agrave;n c&oacute; thể th&aacute;o ruột b&uacute;t v&agrave; thay ch&uacute;ng khi hết mực, c&oacute; nghĩa l&agrave; bạn c&oacute; thể t&aacute;i sử dụng b&uacute;t trong nhiều năm!</p>\r\n\r\n<p>ĐIỀU CHỈNH THEO MONG MUỐN</p>\r\n\r\n<p>Khắc laze l&ecirc;n tr&ecirc;n chiếc b&uacute;t của bạn để tạo ra những m&oacute;n qu&agrave; c&aacute; nh&acirc;n đ&aacute;ng y&ecirc;u cho bạn b&egrave;, gia đ&igrave;nh, kh&aacute;ch h&agrave;ng hay cộng sự của bạn.</p>\r\n\r\n<p>ĐẶC ĐIỂM</p>\r\n\r\n<ul>\r\n	<li>\r\n	<p>Sử dụng tre 100% tự nhi&ecirc;n</p>\r\n	</li>\r\n	<li>\r\n	<p>Thiết kế thủ c&ocirc;ng v&agrave; bền</p>\r\n	</li>\r\n	<li>\r\n	<p>Kh&ocirc;ng sử dụng keo nh&acirc;n tạo hay sơn m&agrave;i</p>\r\n	</li>\r\n	<li>\r\n	<p>B&uacute;t d&agrave;i khoảng 15cm&nbsp;</p>\r\n	</li>\r\n	<li>\r\n	<p>C&oacute; thể điều chỉnh logo theo mong muốn của bạn (Kh&ocirc;ng t&iacute;nh ph&iacute;)</p>\r\n	</li>\r\n	<li>\r\n	<p>Thiết kế độc đ&aacute;o cho ph&eacute;p thay thế ruột b&uacute;t để c&oacute; thể sử dụng nhiều lần</p>\r\n	</li>\r\n</ul>', NULL, NULL, 0, '2020-05-17 03:22:34', '2020-05-18 08:27:50');
INSERT INTO `products` VALUES (107, 'Nước Hoa Thực Vật / Botanical Perfume', 'nuoc-hoa-thuc-vat-botanical-perfume', 'uploads/product/1589685896_nuoc hoa thuc vat.jpg', 2, 429000, 429000, 34, 1, 0, 0, 56, NULL, NULL, 19, 12, NULL, '<pre>\r\n\r\n&nbsp;</pre>\r\n\r\n<p>Dung t&iacute;ch: 10 ml&nbsp;</p>\r\n\r\n<p>Xuất xứ: Việt Nam&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&agrave;nh phần ch&iacute;nh: Dầu nền thực vật, tinh dầu nguy&ecirc;n chất, Vitamin A, Vitamin B, Vitamin B12</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ocirc;ng dụng:</p>\r\n\r\n<p>Tạo hương thơm nhẹ nh&agrave;ng,thu h&uacute;t v&agrave; kh&ocirc;ng g&acirc;y kh&ocirc; v&agrave; k&iacute;ch ứng l&agrave;n da</p>\r\n\r\n<p>M&ugrave;i hương tinh dầu tinh khiết gi&uacute;p thư gi&atilde;n</p>\r\n\r\n<p>Hỗ trợ l&agrave;m ẩm, mềm mịn v&ugrave;ng da được thoa</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điểm đặc biệt:</p>\r\n\r\n<p>Kh&ocirc;ng chất bảo quản, parabens - Non Preservatives &amp; Parabens</p>\r\n\r\n<p>Kh&ocirc;ng chất tạo m&ugrave;i - Non Artificial Scent</p>\r\n\r\n<p>Kh&ocirc;ng m&agrave;u h&oacute;a học - Non Artificial Colors</p>\r\n\r\n<p>Kh&ocirc;ng chất hoạt động bề mặt - Non Surfactants</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;ch sử dụng: Lăn trực tiếp nước hoa v&agrave;o cổ tay, sau tai, sau cổ hoặc bất kỳ nơi n&agrave;o bạn muốn lưu lại hương thơm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hạn sử dụng: Tốt nhất trong v&ograve;ng 1 năm từ lần d&ugrave;ng đầu ti&ecirc;n</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;ch Bảo Quản: Bảo quản nơi tho&aacute;ng m&aacute;t, tr&aacute;nh &aacute;nh nắng trực tiếp</p>', NULL, NULL, 0, '2020-05-17 03:24:56', '2020-05-17 14:42:32');
INSERT INTO `products` VALUES (108, 'Bột khử mùi & kiềm mồ hôi 50g/ Deodorant', 'bot-khu-mui-kiem-mo-hoi-50g-deodorant', 'uploads/product/1589685967_bot khu mui kiem mo hoi.jpg', 4, 329000, 319000, 34, 1, 0, 0, 56, 'https://www.laidayrefill.com/product-page/b%E1%BB%99t-kh%E1%BB%AD-m%C3%B9i-ki%E1%BB%81m-m%E1%BB%93-h%C3%B4i-50g-deodorant', NULL, 19, 12, NULL, NULL, NULL, NULL, 0, '2020-05-17 03:26:07', '2020-05-17 03:26:07');
INSERT INTO `products` VALUES (109, 'Túi lưới đi chợ - 100% cotton / Reusable Produce 100% Cotton Mesh Bag', 'tui-luoi-di-cho-100-cotton-reusable-produce-100-cotton-mesh-bag', 'uploads/product/1589686043_tui luoi di cho.jpg', 20, 25000, 25000, 36, 1, 0, 0, 56, NULL, NULL, 19, 12, NULL, NULL, NULL, NULL, 0, '2020-05-17 03:27:23', '2020-05-19 03:41:55');

-- ----------------------------
-- Table structure for users
-- ----------------------------
DROP TABLE IF EXISTS `users`;
CREATE TABLE `users`  (
  `id` int(10) UNSIGNED NOT NULL AUTO_INCREMENT,
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `email` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `password` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL,
  `phone` text CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL,
  `remember_token` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `role_id` int(11) NOT NULL,
  `is_active` int(11) NOT NULL,
  `avatar` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  `created_at` timestamp(0) NULL DEFAULT NULL,
  `updated_at` timestamp(0) NULL DEFAULT NULL,
  `address` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NULL DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  UNIQUE INDEX `users_email_unique`(`email`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 18 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_unicode_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of users
